export const claimOwnerFundsOperation = async () => {
    try {
        const contract = await tezos.wallet.at("KT1DeqQPVMYzmqvZR9e4A5k2aTbCyYfuJf4H");
        const op = await contract.methods.claimOwner().send()
       await op.confirmation(1);
    }
    catch(err) {
        throw err;
    }
}

export const claimCounterpartyFundsOperation = async (hash) => {
    try {
        const contract = await tezos.wallet.at("KT1DeqQPVMYzmqvZR9e4A5k2aTbCyYfuJf4H");
        const op = await contract.methods.claimCounterparty(hash).send()
       await op.confirmation(1);
    }
    catch(err) {
        throw err;
    }
}

export const addBalanceCounterpartyOperation = async (amount) => {
    try {
        const contract = await tezos.wallet.at("KT1DeqQPVMYzmqvZR9e4A5k2aTbCyYfuJf4H");
        const op = await contract.methods.addBalanceCounterparty().send({
            amount: amount,
            mutez: false,
        })
       await op.confirmation(1);
    }
    catch(err) {
        throw err;
    }
}
