import { useState, useEffect } from "react";

// Components
import { connectWallet, getAccount } from "./utils/wallet";
// Operation
// import { buyTicketOperation, endGameOperation } from "./utils/operation";
import { fetchStorage } from "./utils/tzkt";
import { claimOwnerFundsOperation, claimCounterpartyFundsOperation, addBalanceOwnerOperation, addBalanceCounterpartyOperation } from "./utils/operation";

const App = () => {

  // Players holding lottery tickets
  const [ownerHash, setOwnerHash] = useState("");
  const [counterpartyHash, setCounterpartyHash] = useState("");
  const [ownerBalance, setOwnerBalance] = useState(0);
  const [counterpartyBalance, setCounterpartyBalance] = useState(0);
  const [fromOwner, setFromOwner] = useState(0);
  const [fromCounterparty, setFromCounterparty] = useState(0);
  const [loading, setLoading] = useState(false);

  const [secretKey, setSecretKey] = useState("");
  const [inputSecretKey, setInputSecretKey] = useState("");

  const [inputFunds, setInputFunds] = useState(1);

  // Set players and tickets remaining
  useEffect(() => {
    // TODO 9 - Fetch parties' hashes and their balances from storage
    (async () => {
      const storage = await fetchStorage();
      setOwnerHash(storage.owner);
      setCounterpartyHash(storage.counterparty);
      setOwnerBalance(storage.ownerBalance);
      setCounterpartyBalance(storage.counterpartyBalance);
      setFromOwner(storage.fromOwner);
      setFromCounterparty(storage.fromCounterparty);

      setSecretKey(storage.hashedSecret);
    })();
  }, []);

  // Get the active account
  useEffect(() => {
    (async () => {
      // TODO 5.b - Get the active account
      const account = await getAccount();
      setAccount(account);
    })();
  }, []);

  const [account, setAccount] = useState("");

  useEffect(() => {
    (async () => {
      // TODO 5.b - Get the active account
      const account = await getAccount();
      setAccount(account);
    })();
  }, []);

  // TODO 4.a - Complete onConnectWallet function
  const onConnectWallet = async () => {
    await connectWallet();
    const account = await getAccount();
    setAccount(account);
  };

  const onClaimFunds = async () => {
    try {
      setLoading(true);
      console.log(account === ownerHash);
      if (account) {
        if (account === ownerHash){
            await claimOwnerFundsOperation();
            alert("Owner claimed!")
        }
        else if (account === counterpartyHash) {
            if (secretKey === inputSecretKey) {
              await claimCounterpartyFundsOperation(inputSecretKey);
              alert("Counterparty claimed!");
            }
            else {
              alert("Secret key incorrect!")
            }
        }    
        else {
            console.log(ownerHash);
            console.log(account);
            alert("Not authorised to claim!");
        }
      }
    } catch(err) {
      alert("Transaction Failed: ", err.message);
    }
    setLoading(false);
  };

  const onDepositFunds = async () => {
    try {
      setLoading(true);
      console.log(account === ownerHash);
      if (account) {
        if (account === ownerHash){
          console.log("Owner trying to deposit.");
            await addBalanceOwnerOperation(inputFunds);
            alert("Owner deposited funds!")
        }
        else if (account === counterpartyHash) {
          console.log("Counterparty trying to deposit.");
            await addBalanceCounterpartyOperation(inputFunds);
            alert("Counterparty deposited funds!");
        }    
        else {
            console.log(ownerHash);
            console.log(account);
            alert("Not authorised to deposit fundss!");
        }
      }
    } catch(err) {
      alert("Transaction Failed: ", err.message);
    }
    setLoading(false);
  };

  return (
    <>
    <div className="h-100">
      
      <div className="navbar navbar-dark bg-dark fixed-top">
      <div className="container py-2">
        <a href="/" className="navbar-brand">
          Tezos Escrow Contract
        </a>
        <div className="d-flex">
          {/* TODO 4.b - Call connectWallet function onClick  */}
          <button onClick={onConnectWallet} className="btn btn-outline-info">
            {/* TODO 5.a - Show account address if wallet is connected */}
            { account ? account : "Connect Wallet"}
          </button>
        </div>
      </div>
    </div>
    </div>
      
      <div className="d-flex flex-column justify-content-center align-items-center h-100">
        
        <div className="upper-card-set">
          <div className="card-3s">
            <div className="card-name">Your Address</div> <div className="card-content">{account}</div>
          <div className="card-3s">Owner Address: {ownerHash}</div>
          <div className="card-3s">Counterparty Address: {counterpartyHash}</div>
        </div>
        <div className="py-1">From Owner: {fromOwner/1000000}</div>
        <div className="py-1">From Counterparty: {fromCounterparty/1000000}</div>
        
        <div>
          <form>
            <label>
              Amount:
              <input type="number" min="1" value={inputFunds} onChange={(e) => setInputFunds(e.target.value)} className="form-control"/>
            </label>
            <button type="button" onClick={onDepositFunds} className="btn btn-primary btn-lg">
              { loading ? "Loading..." : "Deposit Funds" }
            </button>
            <label>
              Secret Key:
              <input type="text" value={inputSecretKey} onChange={(e) => setInputSecretKey(e.target.value)} className="form-control"/>
            </label>
            <button type="button" onClick={onClaimFunds} className="btn btn-primary btn-lg">
              { loading ? "Loading..." : "Claim Funds" }
            </button>
          </form>
        </div>
      </div>
    </div>
    </>
  );
};

export default App;
