import { useState, useEffect } from "react";

// Components
import Navbar from "./components/Navbar";
import { getAccount } from "./utils/wallet";
// Operation
// import { buyTicketOperation, endGameOperation } from "./utils/operation";
import { fetchStorage } from "./utils/tzkt";
import { claimOwnerFundsOperation, claimCounterpartyFundsOperation, addBalanceOwnerOperation, addBalanceCounterpartyOperation } from "./utils/operation";

const App = () => {
  // Current active account
  const [account, setAccount] = useState("");

  // Players holding lottery tickets
  const [ownerHash, setOwnerHash] = useState("");
  const [counterpartyHash, setCounterpartyHash] = useState("");
  const [ownerBalance, setOwnerBalance] = useState(0);
  const [counterpartyBalance, setCounterpartyBalance] = useState(0);
  const [fromOwner, setFromOwner] = useState(0);
  const [fromCounterparty, setFromCounterparty] = useState(0);
  const [loading, setLoading] = useState(false);

  // Set players and tickets remaining
  useEffect(() => {
    // TODO 9 - Fetch parties' hashes and their balances from storage
    (async () => {
      const storage = await fetchStorage();
      setOwnerHash(storage.owner);
      setCounterpartyHash(storage.counterparty);
      setOwnerBalance(storage.ownerBalance);
      setCounterpartyBalance(storage.counterpartyBalance);
      setFromOwner(storage.fromOwner);
      setFromCounterparty(storage.fromCounterparty);
    })();
  }, []);

  // Get the active account
  useEffect(() => {
    (async () => {
      // TODO 5.b - Get the active account
      const account = await getAccount();
      setAccount(account);
    })();
  }, []);

  // TODO 7.a - Complete onBuyTicket function
  // const onBuyTicket = async () => {
  //   try {
  //     setLoading(true);
  //     await buyTicketOperation();
  //     alert("Transaction Confirmed!");
  //   } catch(err) {
  //     alert("Transaction Failed: ", err.message);
  //   }
  //   setLoading(false);
  // };

  // TODO 11.a - Complete onEndGame function
  // const onEndGame = async () => {
  //   try {
  //     setLoading(true);
  //     await endGameOperation();
  //     alert("Transaction Confirmed!");
  //   } catch(err) {
  //     alert("Transaction Failed: ", err.message);
  //   }
  //   setLoading(false);
  // };

  const onClaimFunds = async () => {
    try {
      setLoading(true);
      console.log(account === ownerHash);
      if (account) {
        switch (account){
          case (account === ownerHash):
            await claimOwnerFundsOperation();
            alert("Owner claimed!");
            break;
          case (account === counterpartyHash):
            await claimCounterpartyFundsOperation();
            alert("Counterparty claimed!");
            break;
          default:
            console.log(ownerHash);
            console.log(account);
            alert("Not authorised to claim!");
            break;
        }
      }
    } catch(err) {
      alert("Transaction Failed: ", err.message);
    }
    setLoading(false);
  };

  return (
    // <div className="h-100">
    //   <Navbar />
    //   <div className="d-flex flex-column justify-content-center align-items-center h-100">
    //     {/* Ticket remaining display */}
    //     <div className="py-1">Tickets remaining: {tickets}</div>
    //     {/* Action Buttons */}
    //     {tickets > 0 ? (
    //       <button onClick={onBuyTicket} className="btn btn-primary btn-lg">
    //         {/* TODO 7.b - Call onBuyTicket on click */}
    //         {/* TODO 7.c - Show "loading..." when buying operation is pending */}
    //         { loading ? "Loading..." : "Buy Ticket" }
    //       </button>
    //     ) : (
    //       <button onClick={onEndGame} className="btn btn-success btn-lg">
    //         {/* TODO 11.b - Call onEndGame on click */}
    //         {/* TODO 11.c - Show "loading..." when buying operation is pending */}
    //         { loading ? "Loading..." : "End Game" }
    //       </button>
    //     )}
    //     {/* List of Players */}
    //     <div className="mt-2">
    //       {players.map((player, index) => (
    //         <div key={index}>
    //           <b>Ticket {index + 1}:</b> {player}
    //         </div>
    //       ))}
    //     </div>
    //   </div>
    // </div>

    <div className="h-100">
      <Navbar />
      <div className="d-flex flex-column justify-content-center align-items-center h-100">

        <div className="py-1">Current Active Account: {account}</div>
        <div className="py-1">Owner: {ownerHash}</div>
        <div className="py-1">Counterparty: {counterpartyHash}</div>
        <div className="py-1">Owner Balance: {ownerBalance/1000000}</div>
        <div className="py-1">Counterparty Balance: {counterpartyBalance/1000000}</div>
        <div className="py-1">From Owner: {fromOwner/10000000}</div>
        <div className="py-1">From Counterparty: {fromCounterparty/1000000}</div>
        
        <div>
          <form>
            <label>
              Secret Key:
              <input type="text" name="name" />
            </label>
            <button onClick={onClaimFunds}>
              { loading ? "Loading..." : "Claim Funds" }
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default App;
