  //   } catch(err) {
  //     alert("Transaction Failed: ", err.message);
  //   }
  //   setLoading(false);
  // };

  // TODO 11.a - Complete onEndGame function
  // const onEndGame = async () => {
  //   try {
  //     setLoading(true);
  //     await endGameOperation();
  //     alert("Transaction Confirmed!");
  //   } catch(err) {
  //     alert("Transaction Failed: ", err.message);
  //   }
  //   setLoading(false);
  // };

  const onClaimFunds = async () => {
    try {
      setLoading(true);
      if (account) {
        switch (account){
          case (account == ownerHash):
            await claimOwnerFundsOperation();
            break;
          case (account == counterpartyHash):
            await claimCounterpartyFundsOperation();
            break;
          default:
            alert("Not authorised to claim!");
            break;
        }
      }
    } catch(err) {
      alert("Transaction Failed: ", err.message);
    }
    setLoading(false);
  };

  return (
    // <div className="h-100">
    //   <Navbar />
    //   <div className="d-flex flex-column justify-content-center align-items-center h-100">
    //     {/* Ticket remaining display */}
    //     <div className="py-1">Tickets remaining: {tickets}</div>
    //     {/* Action Buttons */}
    //     {tickets > 0 ? (
    //       <button onClick={onBuyTicket} className="btn btn-primary btn-lg">
    //         {/* TODO 7.b - Call onBuyTicket on click */}
    //         {/* TODO 7.c - Show "loading..." when buying operation is pending */}
    //         { loading ? "Loading..." : "Buy Ticket" }
    //       </button>
    //     ) : (
    //       <button onClick={onEndGame} className="btn btn-success btn-lg">
    //         {/* TODO 11.b - Call onEndGame on click */}
    //         {/* TODO 11.c - Show "loading..." when buying operation is pending */}
    //         { loading ? "Loading..." : "End Game" }
    //       </button>
    //     )}
    //     {/* List of Players */}
    //     <div className="mt-2">
    //       {players.map((player, index) => (
    //         <div key={index}>
    //           <b>Ticket {index + 1}:</b> {player}
    //         </div>
    //       ))}
    //     </div>
    //   </div>
    // </div>

    <div className="h-100">
      <Navbar />
      <div className="d-flex flex-column justify-content-center align-items-center h-100">

        <div className="py-1">Tickets remaining: {tickets}</div>

      </div>
    </div>
  );
};

export default App;
