import { Col, Row, Statistic } from 'antd';

const Deadline = (epoch) => {

  const { Countdown } = Statistic;
  const deadline =  epoch - Date.now();
  console.log("deadline " + epoch);
  const onFinish = () => {
    console.log('finished!');
  };
  const onChange = (val) => {
    if (typeof val === 'number' && 4.95 * 1000 < val && val < 5 * 1000) {
      console.log('changed!');
    }
  };

  return(

      <Countdown title="Deadline" value={deadline} format="D [Days] H [Hours] m [Minutes] s [Seconds]" />
  )
    };
export default Deadline;
