import { Alert, Space } from 'antd';
const AlertMessage = ({type, description}) => (
  <>
    <Space
      direction="vertical"
      style={{
        width: '100%',
      }}
    >

      <Alert
        message={type}
        description={description}
        type={type.toLowerCase()}
        showIcon
        closable
      />
    
    </Space>
    <div className="vertical-space">  </div>
  </>
);
export default AlertMessage;
